"use strict";

var generateTypeFilters = require('./generateTypeFilters');

var generateTypeSortings = require('./generateTypeSortings');

var generateTypeQueries = require('./generateTypeQueries');

module.exports = extracted => {
  var filters = generateTypeFilters(extracted.types);
  var sortings = generateTypeSortings(extracted.types);
  var queries = generateTypeQueries(extracted.types, filters, sortings);
  var types = extracted.types.concat(filters).concat(sortings);
  return {
    types,
    queries,
    interfaces: extracted.interfaces,
    generation: 'gen2'
  };
};